// SignIn.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Container from '../../components/layout/Container';
import Text from '../../components/text/size';
import BasicInput from '../../components/input/BasicInput';
import PasswordInput from '../../components/input/PasswordInput';
import CheckboxWithLabel from '../../components/checkbox/CheckboxWithLabel';
import S from './style';
import BasicButton from '../../components/button/BasicButton';

const helperLinks = [
    { label: "아이디 찾기", to: "/find-id" },
    { label: "비밀번호 찾기", to: "/find-password" },
];

const SignIn = () => {
  const [id, setId] = useState('');
  const [pw, setPw] = useState('');
  const [rememberId, setRememberId] = useState(false);

  useEffect(() => {
    const savedId = localStorage.getItem('rememberedId');
    if (savedId) {
      setId(savedId);
      setRememberId(true);
    }
  }, []);

  const handleRememberId = (checked) => {
    setRememberId(checked);
    if (!checked) {
      localStorage.removeItem('rememberedId');
    } else if (id.trim() !== '') {
      localStorage.setItem('rememberedId', id);
    }
  };

  useEffect(() => {
    if (rememberId) {
      if (id.trim() !== '') {
        localStorage.setItem('rememberedId', id);
      } else {
        localStorage.removeItem('rememberedId');
      }
    }
  }, [id, rememberId]);

  const isActive = id.trim() !== '' && pw.trim() !== '';

  return (
    <Container>
      <S.LoginWrapper>
          <Text.H3>로그인</Text.H3>
          <S.InputWrapper>
            <BasicInput
              type="text"
              placeholder="아이디를 입력하세요"
              value={id}
              onChange={e => setId(e.target.value)}
            />
            <PasswordInput
              placeholder="비밀번호를 입력하세요"
              value={pw}
              onChange={e => setPw(e.target.value)}
            />
          </S.InputWrapper>

          <S.Options>
            <CheckboxWithLabel
              label="아이디 기억하기"
              checked={rememberId}
              onChange={e => handleRememberId(e.target.checked)}
            />

            <S.LinkGroup>
              {helperLinks.map(({ label, to }) =>
                label === "비밀번호 찾기" ? (
                  <S.PasswordText key={to}>
                    <Text.Body3
                      as={Link}
                      to={to}
                      mr="16px"
                    >
                      {label}
                    </Text.Body3>
                  </S.PasswordText>
                ) : (
                  <Text.Body3
                    as={Link}
                    key={to}
                    to={to}
                    mr="16px"
                  >
                    {label}
                  </Text.Body3>
                )
              )}
            </S.LinkGroup>
          </S.Options>

          <S.ButtonWrapper>
            <BasicButton
              basicButton="large"
              variant="filled"
              disabled={!isActive}
            >
              로그인
            </BasicButton>
            <BasicButton basicButton="large" variant="default">
              회원가입
            </BasicButton>
          </S.ButtonWrapper>
      </S.LoginWrapper>
    </Container>
  );
};

export default SignIn;
