import styled from "styled-components";
import {spacingProps} from "../../styles/spacingProps";
import {flexSpaceBetween } from "../../styles/common";

const S = {}

S.LoginWrapper = styled.div`
    width: 600px;
    margin: auto;
    padding: 150px 0;
    ${spacingProps}

    h3 {
        text-align: center;
        color: ${({ theme }) => theme.PALLETE.primary.main};
    }
`

S.InputWrapper = styled.div`
    display: flex;
    flex-direction: column;
    gap: 20px 0; 
    margin-top: 40px;
`


S.Options = styled.div`
    ${flexSpaceBetween}
    margin-top: 16px;
`

S.LinkGroup = styled.div`
    display: flex;
`

S.PasswordText = styled.div`
  padding-left: 8px;
  margin-left: 8px;
  background-color: ${({ theme }) => theme.PALLETE.background.gray100};o

  &::before {
    content: "";
    position: absolute;
    width: 1px;
    height: 14px;
    background-color: ${({ theme }) => theme.PALLETE.line.disabled.weak};
    left: 0;
    top: 50%;
    transform: translateY(-50%);
  }
`;

export default S;
