import React, { useState } from 'react'; 
import PopupCard from './PopupCard';
import BasicButton from "../button/BasicButton";
import { ReactComponent as ArrowRightIcon } from "../../components/icons/arrow-right.svg";

const PopupTest = () => {
    const [isOpen, setIsOpen] = useState(false); // ✅ 상태 선언

    return (
        <div>

            <BasicButton basicButton="superSmall" variant="gray" onClick={() => setIsOpen(true)}>
                팝업열기
                <ArrowRightIcon width={30} height={30}/>
            </BasicButton>

            {isOpen && (
                <PopupCard
                    title="친구가 추천을 보냈어요!"
                    description="지금 추천하면 포인트가 쌓여요!"
                    onClose={() => setIsOpen(false)}
                    actions={[
                        {
                            label: '추천하기',
                            onClick: () => {
                                console.log('추천!');
                                setIsOpen(false);
                            },
                            type: 'primary',
                        },
                        {
                            label: '닫기',
                            onClick: () => setIsOpen(false),
                            type: 'gray',
                        },
                    ]}
                />
            )}
        </div>
    );
};

export default PopupTest;
