import styled from 'styled-components';

const S = {};

S.AccordionWrapper = styled.div`
  border-bottom: 1px solid #eee;
  background: #f9f9f9;
    ${spacingProps}
`;

S.QuestionBox = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px 24px;
  background: #fff;
  cursor: pointer;

  svg {
    width: 30px;
    height: 30px;
  }
`;



S.AnswerBox = styled.div`
  padding: 20px 24px;
  background: #fff;
  font-size: ${({ theme }) => theme.FONT_SIZE.body2};
  color: ${({ theme }) => theme.PALLETE.text.sub};
  line-height: 1.5;
`;

export default S;
