import React from 'react';
import S from './style';
import Text from "../text/size";
import BasicButton from "../button/BasicButton";

const PopupCardLarge = ({ title, description, actions, subText = [], onClose }) => {
    return (
        <S.Dimmed onClick={onClose}>
            <S.WrapperLarge onClick={(e) => e.stopPropagation()}>
                <Text.H5 color={"#CF4B05"}  fontWeight={700}>{title}</Text.H5>

                {description && (
                    <Text.Body3 color={"#333333"} mt="20">
                        {description.split('\n').map((line, i) => (
                            <span key={i} style={{ display: 'block', wordBreak: 'keep-all' , maxWidth: '220px'}}>{line}</span>
                        ))}
                    </Text.Body3>
                )}

                {subText && (
                    <Text.Caption4 color={"#585858"} mt="20">
                        {description.split('\n').map((line, i) => (
                            <span key={i} style={{ display: 'block', wordBreak: 'keep-all' , maxWidth: '220px'}}>{line}</span>
                        ))}
                    </Text.Caption4>
                )}

                <S.ButtonGroup $count={actions.length} mt="40">
                    {actions.map(({ label, onClick, type = 'primary' }, idx) => (
                        <BasicButton
                            basicButton="small"
                            variant={type}
                            key={idx}
                            onClick={onClick}
                        >
                            {label}
                        </BasicButton>
                    ))}
                </S.ButtonGroup>
            </S.WrapperLarge>
        </S.Dimmed>
    );
};

export default PopupCardLarge;
