import styled, { css } from 'styled-components';
import { ReactComponent as ArrowDownIcon } from '../icons/arrow-down.svg';
import {spacingProps} from "../../styles/spacingProps"; // 경로 확인

const S = {};

S.InputWrapper = styled.div`
    display: flex;
    flex-direction: column;
    gap: 20px 0;
    ${spacingProps}
`

S.SelectWrapper = styled.div`
  position: relative;
`;

S.StyledArrow = styled(ArrowDownIcon)`
  transition: transform 0.2s ease;
  ${({ $open }) => $open && css`transform: rotate(180deg);`}
`;

S.OptionList = styled.ul`
  position: absolute;
  top: calc(100% + 4px);
  left: 0;
  width: 100%;
  background: #fff;
  border: 1px solid #ccc;
  border-radius: 8px;
  z-index: 10;
  list-style: none;
  padding: 0;
  margin: 0;
`;

S.OptionItem = styled.li`
  padding: 12px 16px;
  cursor: pointer;

  &.selected {
    background: #f5f5f5;
    font-weight: bold;
  }

  &:hover {
    background: #f0f0f0;
  }
`;

export default S;
