import styled from "styled-components";
import {flexCenter, flexRow} from "../../../styles/common";

const S = {};

S.HeaderWrapper = styled.header`
    ${flexRow};
    background: #FFF5EC;
    padding: 20px 240px;
`;

S.LogoWrapper = styled.div`
    ${flexCenter}
  img {
    width: 183px;
    height: 60px;
  }
`;

S.Menu = styled.nav`
    ${flexCenter};
    gap: 0 75px;
`;

S.RightMenu = styled.div`
    display: flex;
    align-items: center;
    gap: 12px;
`;

S.ProfileImg = styled.img`
    width: 36px;
    height: 36px;
    border-radius: 50%;
    object-fit: cover;
`;

S.Badge = styled.div`
    position: relative;
`;

S.BadgeCount = styled.span`
    position: absolute;
    top: -6px;
    right: -6px;
    background-color: #cf4b05;
    color: white;
    font-size: 10px;
    border-radius: 999px;
    padding: 2px 6px;
`;

export default S;
