import React, { useState } from 'react';
import PopupCard from '../../components/popUp/PopupCard'; // 경로는 너의 구조에 맞게 조정
import BasicButton from '../../components/button/BasicButton';
import PopupCardLarge from "./PopupCardLarge";

const PopUpTest = () => {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <section>
            <BasicButton onClick={() => setIsOpen(true)} variant="filled" roundButton="small">
                팝업 열기
            </BasicButton>

            {isOpen && (
                <PopupCardLarge
                    title="로그인 실패"
                    description="입력하신 아이디 또는 비밀번호가 일치하지 않습니다."
                    subText=""
                    onClose={() => setIsOpen(false)}
                    actions={[
                        {
                            label: '추천하기',
                            onClick: () => {
                                console.log('추천 완료!');
                                setIsOpen(false);
                            },
                            type: 'filled',
                        },
                        {
                            label: '닫기',
                            onClick: () => setIsOpen(false),
                            type: 'gray',
                        },
                    ]}
                />
            )}
        </section>
    );
};

export default PopUpTest;
