import React from 'react';
import S from './style';
import BasicInput from './BasicInput';
import BasicButton from '../button/BasicButton';

const ButtonWithInput = ({
                             status = 'default',
                             placeholder = '',
                             buttonText = '',
                             onClick,
                             variant = 'default',         // ✅ 추가
                             roundButton = 'superSmall',  // ✅ 추가
                             basicButton,                 // ✅ 선택적으로 받을 수도 있음
                             mt = '0',
                             ...rest
                         }) => {
    return (
        <S.InputButtonWrapper mt={mt}>
            <BasicInput
                status={status}
                type="text"
                placeholder={placeholder}
                {...rest}
            />
            <BasicButton
                variant={variant}
                roundButton={roundButton}
                basicButton={basicButton} // optional
                onClick={onClick}
            >
                {buttonText}
            </BasicButton>
        </S.InputButtonWrapper>
    );
};

export default ButtonWithInput;
