import React, { useState } from 'react';
import PopupCard from '../../components/popUp/PopupCard'; // 경로는 너의 구조에 맞게 조정
import BasicButton from '../../components/button/BasicButton';

const PopUpTest = () => {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <section>
            <BasicButton onClick={() => setIsOpen(true)} variant="filled" roundButton="small">
                팝업 열기
            </BasicButton>

            {isOpen && (
                <PopupCardLarge
                    title="친구가 추천을 보냈어요!"
                    description="지금 바로 추천하면 포인트가 쌓여요!"
                    onClose={() => setIsOpen(false)}
                    actions={[
                        {
                            label: '추천하기',
                            onClick: () => {
                                console.log('추천 완료!');
                                setIsOpen(false);
                            },
                            type: 'filled',
                        },
                        {
                            label: '닫기',
                            onClick: () => setIsOpen(false),
                            type: 'gray',
                        },
                    ]}
                />
            )}
        </section>
    );
};

export default PopUpTest;
