import styled, { css } from 'styled-components';

const S = {};

S.RadioWrapper = styled.div`
    display: inline-flex;
    align-items: center;
    gap: 8px;
    cursor: pointer;
`;

S.LabelWrapper = styled.label`
  display: inline-flex;
  align-items: center;
  gap: 8px;
  cursor: pointer;

  span {
    ${({ size }) => {
    switch (size) {
        case 'L': return css`font-size: 24px; font-weight: 600;`;
        case 'M': return css`font-size: 18px; font-weight: 500;`;
        case 'S': return css`font-size: 14px; font-weight: 400;`;
        default: return null;
    }
}}
  }
`;

export default S;
