import styled from "styled-components";

const S = {};

S.HeaderWrapper = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #fff5ee;
  padding: 20px 240px;
`;

S.LogoWrapper = styled.div`
  display: flex;
  align-items: center;
  img {
    width: 120px;
    height: auto;
  }
`;


S.Menu = styled.nav`
  display: flex;
  gap: 32px;
`;

S.MenuItem = styled.span`
  font-size: 16px;
  color: #000;
`;

S.RightMenu = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
`;

S.ProfileImg = styled.img`
  width: 36px;
  height: 36px;
  border-radius: 50%;
  object-fit: cover;
`;

S.Badge = styled.div`
  position: relative;
`;

S.BadgeCount = styled.span`
  position: absolute;
  top: -6px;
  right: -6px;
  background-color: #cf4b05;
  color: white;
  font-size: 10px;
  border-radius: 999px;
  padding: 2px 6px;
`;

export default S;
